def smooth_rate(matchup, alpha=1.0, beta=1.0):
    p_hat = matchup_probabilities[matchup]
    n = matchup_n[matchup]
    return (p_hat*n + alpha) / (n + alpha + beta)

def calc_prob(favorite, underdog):
    matchup = (favorite, underdog)

    if favorite == underdog:
        return 0.5 # this is technically pointless

    if matchup in matchup_probabilities:
        return smooth_rate(matchup)
    else:
        fav_win_pct = seed_win_probabilities[favorite]
        und_win_pct = seed_win_probabilities[underdog]
        return fav_win_pct / (fav_win_pct + und_win_pct)